import { getEcharts } from "../api/api.js";
import * as echarts from 'echarts';
export default {
  name: "EchartsDemo",
  data() {
    return {
      chartData: [
        /*         {
                        "name": "数据1",
                        "value": 10
                    },
                    {
                        "name": "数据2",
                        "value": 20
                    },
                    {
                        "name": "数据3",
                        "value": 30
                    } */
      ]
    };
  },
  created() {},
  mounted() {
    this.getList().then(data => {
      this.chartData = data;
    });
    const chart = echarts.init(this.$refs.echartsContainer);
    chart.setOption(this.getOption());
  },
  methods: {
    getList() {
      getEcharts().then(res => {
        this.chartData = res.data.data_list;
      });
    },
    getOption() {
      const xAxisData = this.chartData.map(item => item.name);
      const seriesData = this.chartData.map(item => item.value);
      return {
        xAxis: {
          type: 'category',
          data: xAxisData
        },
        yAxis: {
          type: 'value'
        },
        series: [{
          type: 'bar',
          data: seriesData
        }]
      };
    }
  }
};
